<?phpclass FuturePay_FuturePay_Block_Form_Futurepay extends Mage_Payment_Block_Form{    protected function _construct()    {		$mark = Mage::getConfig()->getBlockClassName('core/template');        $mark = new $mark;        $mark->setTemplate('futurepay/form/mark.phtml')            ->setPaymentAcceptanceMarkSrc($this->getSkinUrl('futurepay/fp_small_logo.png'))        ;		        $this->setTemplate('futurepay/form/redirect.phtml')            ->setRedirectMessage(                Mage::helper('paypal')->__('Buy Now and Pay Later without a credit card using FuturePay. It\'s secure, convenient and easy to use. Don\'t have an account yet? You can signup and checkout in seconds.')            )            ->setMethodTitle('') // Output PayPal mark, omit title            ->setMethodLabelAfterHtml($mark->toHtml())        ;		parent::_construct();    }}