<?phpclass FuturePay_FuturePay_Block_Info_Futurepay extends Mage_Payment_Block_Info {    protected $_checkout;    protected function _construct() {        parent::_construct();                // override the default info block template        $this->setTemplate('futurepay/info/futurepay.phtml');    }        /**     * Override default getPayableTo() to return an empty string     *     * @return string     */    public function getPayableTo() {        return '';    }        /**     * Override default getMailingAddress() to return an empty string     *     * @return string     */    public function getMailingAddress() {        return '';    }        /**     * Override default getCheckout()     *     * @return string     */    public function getCheckout() {        if (is_null($this->_checkout)) {            $this->_convertAdditionalData();        }        return $this->_checkout;    }        /**     * Override default getReturnUrl() to return an empty string     *     * @return string     */    public function getReturnUrl() {        return '';    }    /**     * Convert additional data to strings and return this instance     *     * @return Mage_Payment_Block_Info_FuturePay     */    protected function _convertAdditionalData() {        $details = @unserialize($this->getInfo()->getAdditionalData());        if (is_array($details)) {            $this->_payableTo = isset($details['payable_to']) ? (string) $details['payable_to'] : '';            $this->_mailingAddress = isset($details['mailing_address']) ? (string) $details['mailing_address'] : '';            $this->_return_url = isset($details['return_url']) ? (string) $details['return_url'] : '';            $this->_checkout = isset($details['checkout']) ? (string) $details['checkout'] : '';            $this->_merchantID = isset($details['merchantID']) ? (string) $details['merchantID'] : '';            $this->_secure_code = isset($details['secure_code']) ? (string) $details['secure_code'] : '';                    } else {            $this->_payableTo = '';            $this->_mailingAddress = '';            $this->_return_url = '';            $this->_checkout = '';            $this->_merchantID = '';            $this->_secure_code = '';        }        return $this;    }        /**     * Override default toPdf() function to return the FuturePay info template     * as HTML     *      * @return string     */    public function toPdf() {        $this->setTemplate('futurepay/info/futurepay.phtml');        return $this->toHtml();    }}